import React, { useState } from 'react'
import styles from './styles.module.scss'

const [passId, setPassId] = useState("")

const handleInput = (e) => {
  setPassId(e.target.value)
}

const handleSubmit = async (passId) => {
      try {
        await fetch ('http://localhost:3001/login', {
          method: "PUT",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(passId),
        })
      } catch(err) { console.log(err) }
}

// TODO take input and pass onto api endpoint
// endpoint should redirect to fire page if successful 
// optional: validate input string legth in the FE here
const Login = () => {
  return(
    <div >
        <input onChange={() => handleInput} placeholder={"enter your pass identifier"}/>
        <button onClick={() => handleSubmit}>Start Burning</button>
    </div>
  )
}

export default Login
