import Head from 'next/head'
import EditableContainer from '../components/editableContainer'
import Countdown from '../components/countdown'
import EditableHeadline from '../components/editableHeadline'

export default function Home({ data }) {


  const primaryFire = data.fires.primaryFire 
  const secondaryFire = data.fires.secondaryFire 
  const dumpsterFire = data.fires.dumpsterFire

  const primaryTitle = data.fires.primaryTitle
  const secondaryTitle = data.fires.secondaryTitle
  const dumpsterTitle = data.fires.dumpsterTitle

  const userId = data.fires.user

  // const primaryFire = [
  //   {
  //     id: "5f54d75b114c6d176d7e9765",
  //     html: "First Task",
  //     shot: true,
  //   },
  //   {
  //     id: "5f54d75b114c6d176d7e9766",
  //     html: "Second Task",
  //     shot: true,
  //   },
  //   {
  //     id: "5f54d75b114c6d176d7e9767",
  //     html: "Third Task",
  //     shot: false,
  //   },
  // ]
  return (
    <div className="container">
      <Head>
        <title>FireList</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div className="header">
        <div className="headerItem">
          <svg width="48" height="48" viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path fillRule="evenodd" clipRule="evenodd" d="M6.19298 11.2727C5.79388 11.2727 5.44123 11.4236 5.1598 11.7045C4.87838 11.9854 4.72723 12.3374 4.72723 12.7357V18.1221C4.72723 18.5204 4.87838 18.8724 5.1598 19.1533C5.44123 19.4342 5.79388 19.5851 6.19298 19.5851H19.6382V20.8406H8.50063C8.10154 20.8406 7.74888 20.9914 7.46746 21.2723C7.18603 21.5532 7.03489 21.9052 7.03489 22.3036V27.6545C7.03489 28.0529 7.18603 28.4049 7.46746 28.6858C7.74888 28.9667 8.10154 29.1175 8.50063 29.1175H19.6382V35.6277C19.6382 36.0261 19.7894 36.3781 20.0708 36.659C20.3522 36.9399 20.7049 37.0908 21.104 37.0908H28.7725C28.9645 37.0908 29.1077 37.0822 29.2182 37.0654C29.5719 37.0115 42.2583 37.0908 42.2583 37.0908C42.2583 37.0908 43.2727 36.8376 43.2727 35.8252V32.5346V30.5097V28.8138H41.6852H30.2382V12.7357C30.2382 12.3374 30.0871 11.9854 29.8057 11.7045C29.5242 11.4236 29.1716 11.2727 28.7725 11.2727H6.19298Z" fill="url(#paint0_linear)"/>
            <defs>
              <linearGradient id="paint0_linear" x1="24" y1="11.2727" x2="24" y2="37.0909" gradientUnits="userSpaceOnUse">
                <stop stopColor="#D3CD29"/>
                <stop offset="1" stopColor="#FF0000"/>
              </linearGradient>
            </defs>
          </svg>
        </div>
        <div className="headerItem"> <Countdown /> </div>
        <div className="headerItem">
          <div>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M6 7H18" stroke="#333333" strokeLinecap="round"/>
              <path d="M6 12H18" stroke="#333333" strokeLinecap="round"/>
              <path d="M6 17H18" stroke="#333333" strokeLinecap="round"/>
            </svg>
          </div>
        </div>
      </div>
      <div className="burnerList">
        <div className="firstRow">
          <div className="card primaryCard">
            <p className="">main fire</p>
            <EditableHeadline fireType={"primaryFire"} fetchedTitle={primaryTitle} userId={userId}/>
            <EditableContainer fireType={"primaryFire"} fetchedBlocks={primaryFire} userId={userId}/>
          </div>
        </div>

        <div className="secondRow">
          <div className="card secondaryCard cardtopright">
            <p className="">secondary fire</p>
            <EditableHeadline fireType={"secondaryFire"} fetchedTitle={secondaryTitle} userId={userId}/>
            <EditableContainer fireType={"secondaryFire"} fetchedBlocks={secondaryFire} userId={userId}/>
          </div>

          <div className="card secondaryCard cardbottomright" >
            <p className="">dumpster fire</p>
            <EditableHeadline fireType={"dumpsterFire"} fetchedTitle={dumpsterTitle} userId={userId}/>
            <EditableContainer fireType={"dumpsterFire"} fetchedBlocks={dumpsterFire} userId={userId}/>
          </div>
        </div>
      </div>
      <footer>
        <p> Hope you could add some focus to your day with <span className="colorfire">FireList!  🔥 </span>
          Created by  <a className="linky" href="https://twitter.com/nikoatwork"> @nikoatwork </a></p>
      </footer>

      <style jsx>{`
        .colorfire, .linky {
          color: #e55039;
        }
        .container {
          display: flex;
          justify-content: center;
          flex-wrap: wrap;
          max-width: 800px;
          margin: 0 auto;
          height: 1000px;
        }

        .header {
          width: 100%;
          min-height: 80px;
          height: 5vh;
          display: flex;
          align-items: center;
          margin-top: 10px;
          justify-content: space-between;
          border-bottom: 1px solid #828282;
        }

        .headerItem {
          flex-grow: 1;
          margin: 0 auto;
        }
        .headerItem + .headerItem {
        }
        .headerItem:nth-child(3n-1) {
          display: flex;
          justify-content: center;
        }

        .headerItem:nth-child(3n) {
          display: flex;
          justify-content: right;
        }

        .burnerList {
          width: 100%;
          height: 90vh;
          display: flex;
          margin-top: 15px;
        }


        footer {
          width: 100%;
          height: 100px;
          border-top: 1px solid #eaeaea;
          justify-content: center;
          color: #828282;
          font-size: 12px;
          padding: 1rem;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        .title a {
          color: #0070f3;
          text-decoration: none;
        }

        .title a:hover,
        .title a:focus,
        .title a:active {
          text-decoration: underline;
        }

       .primaryCard {
          text-align: left;
          color: inherit;
          text-decoration: none;
          padding-top: 5px;
        }


        .secondaryCard {
          text-align: left;
          color: inherit;
          text-decoration: none;
          padding: 5px 0 0 5px;
        }

        .firstRow {
          flex-basis: 50%;
          border-right: 1px solid #828282;
        }

        .secondRow {
          flex-basis: 50%;
        }

        .cardtopright {
          height: 50%;
          border-bottom: 1px solid #828282;
        }

        .card h3 {
          margin: 5px 5px 5px 0;
          font-size: 24px;
          font-weight: 700;
        }

        .card p {
          margin: 0;
          font-size: 12px;
        }

        @media (max-width: 600px) {
          .grid {
